    function removeElementByXPath(xpath) {
        const nodesSnapshot = document.evaluate(xpath, document, null, XPathResult.ORDERED_NODE_SNAPSHOT_TYPE, null);
        for (let i = 0; i < nodesSnapshot.snapshotLength; i++) {
            let node = nodesSnapshot.snapshotItem(i);
            if (node.parentNode) {
                node.parentNode.removeChild(node);
            }
        }
    }

    setInterval(() => {
        removeElementByXPath('/html/body/div[5]/div/div/div/div/form/text()');
        removeElementByXPath('/html/body/div[4]/div/div/div/div/form/text()');
