function modifyElementContentWithTextContent(searchText, newContentValue) {
    // Create an XPath expression that matches any div containing the specific text
    const xpathExpression = `//div[contains(text(), '${searchText}')]`;

    // Evaluate the XPath expression
    const snapshotElements = document.evaluate(xpathExpression, document, null, XPathResult.ORDERED_NODE_SNAPSHOT_TYPE, null);
    for (let i = 0; i < snapshotElements.snapshotLength; i++) {
        const element = snapshotElements.snapshotItem(i);
        // We have to check if the element's text content exactly matches our search text
        // This is necessary because 'contains' may match elements that include additional text
        if (element && element.textContent.trim() === searchText) {
            element.innerHTML = newContentValue;
        }
    }
}

